import advent_of_code.year2015.day01 as sut


def test_examples_part1():
    assert sut.floor_calculator_part1("(())") == 0
    assert sut.floor_calculator_part1("()()") == 0

    assert sut.floor_calculator_part1("(((") == 3
    assert sut.floor_calculator_part1("(()(()(") == 3
    assert sut.floor_calculator_part1("))(((((") == 3

    assert sut.floor_calculator_part1("())") == -1
    assert sut.floor_calculator_part1("))(") == -1

    assert sut.floor_calculator_part1(")))") == -3
    assert sut.floor_calculator_part1(")())())") == -3


